| where TimeGenerated >= start and TimeGenerated <= end
| where EventID == 4688
// excluding well known processes
| where NewProcessName !endswith ':\\Windows\\System32\\conhost.exe' and ParentProcessName !endswith ':\\Windows\\System32\\conhost.exe'
