let Categoriesselected = datatable(Category:string) 
      [ 
        {category}
      ];
Categoriesselected 
| join kind=inner
( sentinelscanreport_CL 
| where type_s =~ "Hunting"
| summarize arg_max(TimeGenerated, *) by rulename_s, type_s, query_s, description_s, Category
//| where count_d > 0
| extend ['Rule Name'] = rulename_s, Query =  query_s, ['Last Scan Time']=TimeGenerated, Description = description_s
| extend ['Rule Category'] = Category
| project ['Last Scan Time'], ['Rule Name'], ['Rule Category'], Query, Description
) on $left.Category == $right.['Rule Category']

