let GetSysLogEventsWithHostName = (v_Host_Name:string, v_Host_Ip:string, v_message_filter:string){
Syslog
| where HostName =~ v_Host_Name or HostIP =~ v_Host_Ip
| where SyslogMessage has  v_message_filter
| summarize min(EventTime), max(EventTime), processcount = count() by Computer, HostIP, ProcessName
| top 10 by processcount desc nulls last 
| project Host_Aux_StartTime = min_EventTime, Host_Aux_EndTime = max_EventTime, Computer,  HostIP, ProcessName, processcount
| project-rename Host_UnstructuredName=Computer, HostIPAddess=HostIP
};
// change <Address> value below
GetSysLogEventsWithHostName('{HostName}','{HostIP}','{Message}')

let v_process_name = '{ProcessName}';
let v_Host_Name = '{HostName}';
let v_Host_Ip = '{HostIP}';
let v_message_filter = '{Message}';
let messagecount = 200;
Syslog
| where Computer =~ v_Host_Name or HostIP =~ v_Host_Ip 
| where ProcessName =~ v_process_name
| where SyslogMessage has  v_message_filter
| project EventTime, Computer,  HostIP, ProcessName, SyslogMessage
| top messagecount by EventTime
